import React, { useRef, useEffect } from "react";
import {
  Eye
} from "lucide-react";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";

function TabsComponent() {
  const videoRefs = useRef([]);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.target instanceof HTMLVideoElement) {
            const video = entry.target;
            if (entry.isIntersecting) {
              video.play().catch(() => {});
            } else {
              video.pause();
            }
          }
        });
      },
      { threshold: 0.7 }
    );

    videoRefs.current.forEach((video) => {
      if (video) observer.observe(video);
    });

    return () => {
      videoRefs.current.forEach((video) => {
        if (video) observer.unobserve(video);
      });
    };
  }, []);

  return (
    <div className="flex w-screen justify-center">
      <Tabs defaultValue="artworks" className="w-[900px]">
        {/* ---- Tab Headers ---- */}
        <TabsList className="grid w-full grid-cols-2">
          <TabsTrigger value="artworks">Artworks</TabsTrigger>
          <TabsTrigger value="stories">Stories</TabsTrigger>
        </TabsList>

        {/* ---- Artworks Grid ---- */}
        <TabsContent value="artworks">
          <div className="grid grid-cols-2 md:grid-cols-3 gap-4 p-4">
            {[
              "data:image/jpeg;base64,",
              "data:image/jpeg;base64,",
              "data:image/jpeg;base64,",
              "data:image/jpeg;base64,",
            ].map((src, i) => (
              <div
                key={i}
                className="w-full h-64 overflow-hidden rounded-xl shadow"
              >
                <img
                  src={src}
                  alt={`artwork-${i}`}
                  className="w-full h-full object-contain"
                />
              </div>
            ))}
          </div>
        </TabsContent>

        <TabsContent value="stories">
          <div className="grid grid-cols-4 gap-1">
            {[
              {
                src: "https://www.w3schools.com/html/mov_bbb.mp4",
                views: "13.8M",
              },
              {
                src: "https://interactive-examples.mdn.mozilla.net/media/cc0-videos/flower.mp4",
                views: "9.6M",
              },
              {
                src: "https://www.w3schools.com/html/movie.mp4",
                views: "53.9M",
              },
              {
                src: "https://www.w3schools.com/html/mov_bbb.mp4",
                views: "48.2M",
              },
              {
                src: "https://interactive-examples.mdn.mozilla.net/media/cc0-videos/flower.mp4",
                views: "4.9M",
              },
              {
                src: "https://www.w3schools.com/html/movie.mp4",
                views: "22.9M",
              },
            ].map((reel, i) => (
              <div
                key={i}
                className="relative aspect-[9/16] bg-black overflow-hidden cursor-pointer"
                onClick={() => {
                 
                  console.log("Open reel:", reel.src);
                }}
              >
                <video
                  src={reel.src}
                  muted
                  playsInline
                  loop
                  className="w-full h-full object-cover"
                />

                <div className="absolute bottom-0 w-full bg-gradient-to-t from-black/70 to-transparent p-1 flex items-center justify-start">
                 <Eye className="h-5 text-white"/> <span className="text-white text-xs"> {reel.views}</span>
                </div>
              </div>
            ))}
          </div>
        </TabsContent>
      </Tabs>
    </div>
  );
}

export default TabsComponent;
